<?phprequire_once('config.php');	//Connect to mysql server	$link = mysql_connect(DB_HOST, DB_USER, DB_PASSWORD);	if(!$link) {		die('Failed to connect to server: ' . mysql_error());	}		//Select database	$db = mysql_select_db(DB_DATABASE);	if(!$db) {		die('Unable to select database');	}	$dist_id = $_REQUEST['dist'];$cust_name = $_REQUEST['cust'];$query = "DELETE FROM customers WHERE customer_name='$cust_name' and distributor_id='$dist_id'";mysql_query($query);header('Location: view_customers.php?id='.$dist_id);?>